mapper.insert:
 		INSERT INTO ASSIGNMENT_NOTICE_BOARD_M (NOTICE_ID, NOTICE_TITLE, CUST_ID, ASSIGNMENT_STATUS, HIT, FIRST_CHANGE_DATE, LAST_CHANGE_DATE)
		VALUES ( 
			  #{noticeId}  
			, #{noticeTitle}
			, #{custId}
			, #{assignmentStatus}
			, #{hit}
			, sysdate
			, sysdate 
		)

mapper.select:
select notice_id as noticeId , notice_title as noticeTitle , cust_id as custId , assignment_status as assignmentStatus , hit as hit , first_change_Date as firstChangeDate from ASSIGNMENT_NOTICE_BOARD_M
where notice_id = #{noticeId}
order by notice_id desc

mapper.insert:
 		INSERT INTO ASSIGNMENT_TICKET (NOTICE_ID, PERFORMANCE_ID, RESERVATION_SEAT_INFORMATION, RESERVATION_NUMBER, RECEIVE_CUST_ID, SOLD_YN, FIRST_CHANGE_DATE, LAST_CHANGE_DATE)
		VALUES ( 
			  #{noticeId}  
			, #{performanceId}
			, #{reservationSeatInformation}
			, #{reservationNumber}
			, #{receiveCustId, jdbcType=VARCHAR}
			, 'N'
			, sysdate
			, sysdate 
		)

mapper.select:
select performance_id as performanceId
	 , reservation_seat_information as reservationSeatInformation
  from reservation_info_m
 where reservation_number=#{reservationNumber}
   and cust_id=#{custId}
   and reservation_status='Y'

mapper.update:
update ASSIGNMENT_NOTICE_BOARD_M 
   set HIT = HIT+1
 where notice_id=#{noticeId}

update assignment_ticket 
   set receive_cust_id=#{receiveCustId},
   	   sold_yn='Y',
       last_change_date=sysdate
 where notice_id=#{noticeId} 
   and performance_id=#{performanceId}
   and reservation_seat_information=#{reservationSeatInformation}

		update reservation_info_m
  		   set cust_id=#{receiveCustId},
  		       last_change_date=sysdate
        where performance_id=#{performanceId}
          and reservation_seat_information=#{reservationSeatInformation}
          and cust_id=cust_id
